<script>
$(function(){
  var demo1 = $('.colorpick');
  demo1.colorpickerplus();
  demo1.on('changeColor', function(e,color){
    if(color==null)
      $(this).css('background-color', '#fff').closest(".color-input").val(1);
    else
      $(this).css('background-color', color).closest(".color-input").val('transparent');
  });
});
  $(document).ready(function(){
  
    $(document).on("mouseleave", ".custom_price",function(){
      var pr= $(this).val();
      $("#custom_package").data("price",pr);
      get_total();
    });
  
    $(document).on("change",".upgrade-btn",function(){
      var up_fee= $(this).data("value");
      console.log(up_fee);
      if($(this).prop('checked') == true){
        $(this).closest(".upgrade-box").addClass("upgrade-checked");
        $(this).closest(".extra_pay").val(up_fee);
      }
      else{
        $(this).closest(".upgrade-box").removeClass("upgrade-checked");
        $(this).closest(".extra_pay").val(0);
      }
      get_total();
    });

    $(document).on("click",".select-pricing",function(){
      $(".pricing-package").removeClass("package-select");
      if($(this).prop('checked') == true){
        $(this).closest(".pricing-package").addClass("package-select");
      }
      $(".pricing-option").slideUp();
      var val= $(this).val();
      
      if(val=="opt4"){
        $(".custom_price").attr("readonly",false);
      }
      else{
        $(".custom_price").val("").attr("readonly",true);
      }
      
      $("#"+val).slideDown();
      get_total()
    });
  
    $(document).on("change",".cat_package",function(){
      $(".catoptions").slideUp();
      var val= $(this).val();
      var val= $(this).val();

    if(val == "1"){
        $(".change_bcolor2").removeClass('custom-box package-select');
        $(".change_bcolor3").removeClass('custom-box package-select');
        $(".change_bcolor4").removeClass('custom-box package-select');
      $(".change_bcolor1").addClass('custom-box package-select');
        }
        if(val == "2"){
        $(".change_bcolor1").removeClass('custom-box package-select');
        $(".change_bcolor3").removeClass('custom-box package-select');
        $(".change_bcolor4").removeClass('custom-box package-select');
      $(".change_bcolor2").addClass('custom-box package-select');
        }
        if(val == "3"){
         $(".change_bcolor1").removeClass('custom-box package-select');
        $(".change_bcolor2").removeClass('custom-box package-select');
        $(".change_bcolor4").removeClass('custom-box package-select');
      $(".change_bcolor3").addClass('custom-box package-select');
        }
        if(val == "4"){
         $(".change_bcolor1").removeClass('custom-box package-select');
        $(".change_bcolor2").removeClass('custom-box package-select');
        $(".change_bcolor3").removeClass('custom-box package-select');  
      $(".change_bcolor4").addClass('custom-box package-select');
        }
      $("#"+val).slideDown();
      get_total();
    });
  });
  
      
  var get_total=function(){

    var grand=0;
    var cprice= $(".cat_package").is(':checked')? $('.cat_package:checked').data("value"):0; 
    
    //console.log(cprice);
    
    var pack_price= $('.select-pricing:checked').is(':checked')? $('.select-pricing:checked').data("price"):0;
    console.log(pack_price);
  
    //grand= parseFloat(cprice)+ parseFloat(pack_price);
    grand=  parseFloat(pack_price);
    
    var ex_tot=0;
    
    $('.upgrade-btn:checked').each(function(e){
      var eval= $(this).data("value");
      ex_tot +=(eval=="")?0:parseFloat(eval);
    });
    
  
    grand += ex_tot ;
    
    $('.contest_total').html(grand);
    $('.contest_total').val(grand);
    console.log(grand);
